{
    "name": "doc-enrich",
    "description": null,
    "skills": [
    	{
        "@odata.type": "#Microsoft.Skills.Vision.OcrSkill",
        "context": "/document/normalized_images/*",
        "defaultLanguageCode": "en",
        "detectOrientation": true,
        "inputs": [
          {
            "name": "image",
            "source": "/document/normalized_images/*"
          }
        ],
        "outputs": [
          {
            "name": "text"
          }
        ]
    },
    {
      "@odata.type": "#Microsoft.Skills.Text.MergeSkill",
      "description": "Create merged_text, which includes all the textual representation of each image inserted at the right location in the content field.",
      "context": "/document",
      "insertPreTag": " ",
      "insertPostTag": " ",
      "inputs": [
        {
          "name":"text", 
          "source": "/document/content"
        },
        {
          "name": "itemsToInsert", 
          "source": "/document/normalized_images/*/text"
        },
        {
          "name":"offsets", 
          "source": "/document/normalized_images/*/contentOffset" 
        }
      ],
      "outputs": [
        {
          "name": "mergedText", 
          "targetName" : "merged_text"
        }
      ]
    },
        {
            "@odata.type": "#Microsoft.Skills.Text.SplitSkill",
            "name": "SplitSkill#1",
            "description": null,
            "context": "/document",
            "defaultLanguageCode": "en",
            "textSplitMode": "pages",
            "maximumPageLength": 5000,
            "inputs": [
                {
                    "name": "text",
                    "source": "/document/merged_text"
                }
            ],
            "outputs": [
                {
                    "name": "textItems",
                    "targetName": "pages"
                }
            ]
        },
        {
            "@odata.type": "#Microsoft.Skills.Text.EntityRecognitionSkill",
            "name": "EntityRecognition#2",
            "description": null,
            "context": "/document",
            "categories": [
                "Person",
                "Location",
                "Organization"
            ],
            "defaultLanguageCode": "en",
            "minimumPrecision": 0.5,
            "includeTypelessEntities": null,
            "inputs": [
                {
                    "name": "text",
                    "source": "/document/merged_text"
                }
            ],
            "outputs": [
                {
                    "name": "persons",
                    "targetName": "people"
                },
                {
                    "name": "locations",
                    "targetName": "locations"
                },
                {
                    "name": "organizations",
                    "targetName": "organizations"
                },
                {
                    "name": "namedEntities",
                    "targetName": "entities"
                }
            ]
        },
        {
            "@odata.type": "#Microsoft.Skills.Text.KeyPhraseExtractionSkill",
            "name": "KeyPhrases#3",
            "description": null,
            "context": "/document/pages/*",
            "defaultLanguageCode": "en",
            "maxKeyPhraseCount": null,
            "inputs": [
                {
                    "name": "text",
                    "source": "/document/pages/*"
                }
            ],
            "outputs": [
                {
                    "name": "keyPhrases",
                    "targetName": "keyPhrases"
                }
            ]
        },
        {
            "@odata.type": "#Microsoft.Skills.Util.ShaperSkill",
            "name": "Shaper#4-Case For Table Projections",
            "description": null,
            "context": "/document",
            "inputs": [
                {
                    "name": "Name",
                    "source": "/document/metadata_storage_name"
                }, {
                    "name": "Transcript",
                    "source": "/document/merged_text"
                } , {
                    "name": "DocumentID", 
                    "source": "/document/id"
                }
          
            ],
            "outputs": [
                {
                    "name": "output",
                    "targetName": "Doc"
                }
            ]
        }
       
    ],
    "cognitiveServices": {
        "@odata.type": "#Microsoft.Azure.Search.CognitiveServicesByKey",
        "description": "mbdmis-cognitive-services",
      "key": "{{cog_services_key}}"
    },
    "knowledgeStore": {
        "storageConnectionString":"{{env_storage_connection_string}}",
        "projections": [
            {
                "tables": [
                   
                    {
                        "tableName": "Documents",
                         "generatedKeyName": "DocClassID",
                        "source": "/document/Doc"
                    }
                ],
                "objects": []
            }
            
            
        ]
    }
}
